import{_ as e,c as i,a4 as l,o as t}from"./chunks/framework.DJY4F8Kc.js";const h=JSON.parse('{"title":"让我们跟代码来一场美丽的邂逅吧","description":"","frontmatter":{},"headers":[],"relativePath":"front/encountering/index.md","filePath":"front/encountering/index.md"}'),p={name:"front/encountering/index.md"};function o(r,a,n,c,s,d){return t(),i("div",null,a[0]||(a[0]=[l('<h1 id="让我们跟代码来一场美丽的邂逅吧" tabindex="-1">让我们跟代码来一场美丽的邂逅吧 <a class="header-anchor" href="#让我们跟代码来一场美丽的邂逅吧" aria-label="Permalink to &quot;让我们跟代码来一场美丽的邂逅吧&quot;">​</a></h1><h2 id="完善的应用程序" tabindex="-1">完善的应用程序 <a class="header-anchor" href="#完善的应用程序" aria-label="Permalink to &quot;完善的应用程序&quot;">​</a></h2><ul><li>服务端开发</li><li>ios开发、Android开发</li><li>Web开发</li><li>桌面开发(window mac os)</li></ul><h2 id="前端开发的内容" tabindex="-1">前端开发的内容 <a class="header-anchor" href="#前端开发的内容" aria-label="Permalink to &quot;前端开发的内容&quot;">​</a></h2><ul><li><p>前端开发的任务:</p><p>○ web 开发、小程序开发</p><p>○ 移动端、桌面端 (electron)、服务端开发(Node)</p></li></ul><h2 id="网站和网页的关系" tabindex="-1">网站和网页的关系 <a class="header-anchor" href="#网站和网页的关系" aria-label="Permalink to &quot;网站和网页的关系&quot;">​</a></h2><ul><li><p>网页(Web Page)</p><p>○ 打开浏览器查看到的页面，是网络中的一&quot;页&quot;</p><p>○ 网页的内容可以非常丰富：包括 文字、链接、图片、音乐、视频 等等</p></li><li><p>网站(Web application)</p><p>○ 网站时由多个网页组成的;</p><p>○ 通常一个网站由N个网页组成(N &gt;= 1)</p></li></ul><h2 id="网页的显示过程" tabindex="-1">网页的显示过程 <a class="header-anchor" href="#网页的显示过程" aria-label="Permalink to &quot;网页的显示过程&quot;">​</a></h2><ul><li><p>用户角度</p><p>○ 用户在浏览器输入一个网站</p><p>○ 浏览器会找到对应的服务器地址，请求静态资源(可以存放在世界上任何一个地方)</p><p>○ 服务器返回静态资源给浏览器</p><p>○ 浏览器对静态资源进行解析和展示</p></li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>用户在浏览器输入地址 --&gt; 通过DNS服务器解析 --&gt; ip地址 --&gt; 服务器(资源) --&gt; 浏览器渲染引擎(html css)、js引擎(V8)</p></div><ul><li><p>前端工程师</p><p>○ 开发项目(HTML/CSS/JavaScript/Vue/React)</p><p>○ 打包、部署项目到服务器里面</p></li><li><p>服务器</p><p>○ 服务器本质上也是一台类似于电脑一样的主机</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>1.二十四小时不关机</p><p>2.没有显示器</p><p>3.一般装的是Linux操作系统</p></div></li></ul><h2 id="网页的组成部分" tabindex="-1">网页的组成部分 <a class="header-anchor" href="#网页的组成部分" aria-label="Permalink to &quot;网页的组成部分&quot;">​</a></h2><ul><li>HTML：网页的结构</li><li>CSS：网页的样式(美化)</li><li>javascript：网页的交互(灵魂)</li></ul>',13)]))}const m=e(p,[["render",o]]);export{h as __pageData,m as default};
