import{_ as e,c as i,a4 as l,o as t}from"./chunks/framework.DJY4F8Kc.js";const u=JSON.parse('{"title":"让我们跟代码来一场美丽的邂逅吧","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"front/encountering/indexA.md","filePath":"front/encountering/indexA.md","lastUpdated":null}'),o={name:"front/encountering/indexA.md"};function r(n,a,p,c,d,s){return t(),i("div",null,a[0]||(a[0]=[l('<h1 id="让我们跟代码来一场美丽的邂逅吧" tabindex="-1">让我们跟代码来一场美丽的邂逅吧 <a class="header-anchor" href="#让我们跟代码来一场美丽的邂逅吧" aria-label="Permalink to &quot;让我们跟代码来一场美丽的邂逅吧&quot;">​</a></h1><h2 id="完善的应用程序" tabindex="-1">完善的应用程序 <a class="header-anchor" href="#完善的应用程序" aria-label="Permalink to &quot;完善的应用程序&quot;">​</a></h2><ul><li>服务端开发</li><li>ios 开发、Android 开发</li><li>Web 开发</li><li>桌面开发(window mac os)</li></ul><h2 id="前端开发的内容" tabindex="-1">前端开发的内容 <a class="header-anchor" href="#前端开发的内容" aria-label="Permalink to &quot;前端开发的内容&quot;">​</a></h2><ul><li><p>前端开发的任务:</p><p>○ web 开发、小程序开发</p><p>○ 移动端、桌面端 (electron)、服务端开发(Node)</p></li></ul><h2 id="网站和网页的关系" tabindex="-1">网站和网页的关系 <a class="header-anchor" href="#网站和网页的关系" aria-label="Permalink to &quot;网站和网页的关系&quot;">​</a></h2><h3 id="网页-web-page" tabindex="-1">网页(Web Page) <a class="header-anchor" href="#网页-web-page" aria-label="Permalink to &quot;网页(Web Page)&quot;">​</a></h3><p>○ 打开浏览器查看到的页面，是网络中的一&quot;页&quot;</p><p>○ 网页的内容可以非常丰富：包括 文字、链接、图片、音乐、视频 等等</p><h3 id="网站-web-application" tabindex="-1">网站(Web application) <a class="header-anchor" href="#网站-web-application" aria-label="Permalink to &quot;网站(Web application)&quot;">​</a></h3><p>○ 网站时由多个网页组成的;</p><p>○ 通常一个网站由 N 个网页组成(N &gt;= 1)</p><h2 id="网页的显示过程" tabindex="-1">网页的显示过程 <a class="header-anchor" href="#网页的显示过程" aria-label="Permalink to &quot;网页的显示过程&quot;">​</a></h2><h3 id="用户角度" tabindex="-1">用户角度 <a class="header-anchor" href="#用户角度" aria-label="Permalink to &quot;用户角度&quot;">​</a></h3><p>○ 用户在浏览器输入一个网站</p><p>○ 浏览器会找到对应的服务器地址，请求静态资源(可以存放在世界上任何一个地方)</p><p>○ 服务器返回静态资源给浏览器</p><p>○ 浏览器对静态资源进行解析和展示</p><div class="tip custom-block"><p class="custom-block-title">过程</p><p>用户在浏览器输入地址 --&gt; 通过 DNS 服务器解析 --&gt; ip 地址 --&gt; 服务器(资源) --&gt; 浏览器渲染引擎(html css)、js 引擎(V8)</p></div><h3 id="前端工程师" tabindex="-1">前端工程师 <a class="header-anchor" href="#前端工程师" aria-label="Permalink to &quot;前端工程师&quot;">​</a></h3><p>○ 开发项目(HTML/CSS/JavaScript/Vue/React)</p><p>○ 打包、部署项目到服务器里面</p><ul><li><p>服务器</p><p>○ 服务器本质上也是一台类似于电脑一样的主机</p><div class="tip custom-block"><p class="custom-block-title">特点 1.二十四小时不关机</p><p>2.没有显示器</p><p>3.一般装的是 Linux 操作系统</p></div></li></ul><h2 id="网页的组成部分" tabindex="-1">网页的组成部分 <a class="header-anchor" href="#网页的组成部分" aria-label="Permalink to &quot;网页的组成部分&quot;">​</a></h2><ul><li>HTML：网页的结构</li><li>CSS：网页的样式(美化)</li><li>javascript：网页的交互(灵魂)</li></ul><h2 id="浏览器的内核" tabindex="-1">浏览器的内核 <a class="header-anchor" href="#浏览器的内核" aria-label="Permalink to &quot;浏览器的内核&quot;">​</a></h2><ul><li><p>浏览器最核心的部分是渲染引擎(Rendering Engine),一般也成为浏览器内核</p><p>○ 负责解析网页语法、并渲染(显示)网页</p></li></ul><div class="tip custom-block"><p class="custom-block-title">常见的浏览器内核</p><ol><li>Trident (三叉戟) : IE、360 浏览器、搜狗高速浏览器、百度浏览器、UC 浏览器</li><li>Gecko (壁虎): Mozilla、Firefox</li><li>Presto (急板乐曲) --&gt; Blink (眨眼): Opera</li><li>Webkit Safari、360 极速浏览器、搜狗高速浏览器、移动端浏览器(Android、ios)</li><li>Webkit --&gt; Blink:Googlr Chrome、Edge</li></ol></div>',28)]))}const b=e(o,[["render",r]]);export{u as __pageData,b as default};
